using a7D.PDV.SAT;
using System;
using System.Collections.Generic;
using System.ComponentModel;
using System.Configuration;
using System.Data;
using System.Drawing;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using System.Windows.Forms;

namespace a7D.PDV.TesteImpressaoSAT.UI
{
    public partial class Form1 : Form
    {
        public Form1()
        {
            InitializeComponent();
        }

        private void btnImprimir_Click(object sender, EventArgs e)
        {
            try
            {
                Cursor = Cursors.WaitCursor;
                Refresh();
                var ex = default(Exception);
                ImpressaoSat.ImprimirCupomVenda("", 1, ConfigurationManager.AppSettings["ModeloImpressora"], "Novo", "Elgin", out ex);
                if (ex != null)
                {
                    throw ex;
                }
            }
            catch (Exception e_)
            {
                MessageBox.Show(e_.Message, "Erro", MessageBoxButtons.OK, MessageBoxIcon.Error);
            }
            finally
            {
                Cursor = Cursors.Default;
                Refresh();
            }
        }
    }
}
